import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
import { motion } from 'framer-motion';

const Questionnaire = () => {
  // Use arrays for genres and languages
  const [genres, setGenres] = useState([]);
  const [languages, setLanguages] = useState([]);
  const [mood, setMood] = useState('');
  const navigate = useNavigate();

  // Handler for multi-select dropdowns
  const handleMultiSelectChange = (setter) => (event) => {
    const { options } = event.target;
    const selectedValues = [];
    for (let i = 0, l = options.length; i < l; i++) {
      if (options[i].selected) {
        selectedValues.push(options[i].value);
      }
    }
    setter(selectedValues);
  };

  const handleSubmit = async () => {
    if (!mood || genres.length === 0) {
      alert('Please select a mood and at least one genre.');
      return;
    }
    // Pass all selections to the results page via state
    const res = await api.post('/recommend', { genres, languages, mood });
    navigate('/results', { state: { tracks: res.data.tracks, mood: mood } });
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="flex flex-col items-center space-y-6 mt-12 text-white"
    >
      <div className="w-full max-w-xs">
        <label className="block mb-2">Favorite Genres (hold Ctrl/Cmd to select multiple):</label>
        <select
          multiple
          onChange={handleMultiSelectChange(setGenres)}
          className="w-full p-2 bg-gray-900 rounded h-24"
        >
          <option>Rock</option>
          <option>Pop</option>
          <option>Jazz</option>
          <option>Electronic</option>
          <option>Hip-Hop</option>
        </select>
      </div>

      <div className="w-full max-w-xs">
        <label className="block mb-2">Languages (optional, hold Ctrl/Cmd to select multiple):</label>
        <select
          multiple
          onChange={handleMultiSelectChange(setLanguages)}
          className="w-full p-2 bg-gray-900 rounded h-24"
        >
          <option>English</option>
          <option>Spanish</option>
          <option>Hindi</option>
          <option>French</option>
        </select>
      </div>

      <div className="w-full max-w-xs">
         <label className="block mb-2">Select your mood:</label>
        <select
          onChange={e => setMood(e.target.value)}
          className="w-full p-2 bg-gray-900 rounded"
        >
          <option value="">Choose a mood</option>
          <option value="happy">Happy</option>
          <option value="relaxed">Relaxed</option>
          <option value="energetic">Energetic</option>
        </select>
      </div>

      <button
        onClick={handleSubmit}
        className="px-8 py-3 bg-purple-700 rounded-lg hover:bg-purple-800 transition duration-300 shadow-lg"
      >
        Get Recommendations
      </button>
    </motion.div>
  );
};

export default Questionnaire;
